CREATE TABLE planos_detalhes (plano_id INT NOT NULL, detalhe_id INT NOT NULL, INDEX IDX_4964182E9A8B86CC (plano_id), INDEX IDX_4964182E6B253812 (detalhe_id), PRIMARY KEY(plano_id, detalhe_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE planos ADD CONSTRAINT FK_C98178C105CFD56 FOREIGN KEY (produto_id) REFERENCES produtos (id);
